import math
def adajcent(num):
    s_num = str(num)
    res = []
    for ch in s_num:
        res.append(int(ch))

    max_product = []

    for i in range(len(res)-12):
        window = res[i:i+13]
        val = math.prod(window)
        max_product.append(val)

    return max(max_product)

